    {
        return _path;
    }

    public void SetPath(string path)
    {
        path = _path;
    }

    public override void StartGame()
    {
        GiveInstructions();
        Console.Clear();
        Path1();
        Console.Clear();
        DisplayMessage("Okay, did you see where the captain went?", 50);
        DisplayOptions("I lost the image!", "I think I did before the footage disappeared.", "Yeah, just in time too. I lost the footage.", "Not sure, the image is gone.");
        GetResponse();
        DisplayMessage("Thought that might happen. It looks like you can't talk to me and view the footage at the same time. You'll have to memorize the path.", 50);
        DisplayMessage("I need you to lead to the room he was just in, then to the exit he took. You should be able to see me in the footage. \nSend me the directions when you're ready.", 50);
        Console.Clear();
        InputPath();
    }

    public void InputPath()
    {
        bool isCorrect = false;
        string input = "";
        while (!isCorrect)
        {
        ShowPosition1();
        _path = "";
        input = "";
        Console.WriteLine("Type 'Done' when you're finished.");
        while (input != "Done")
        {
            _path += input;
            input = Console.ReadLine();
        }

        Console.Clear();
        
        if (_path == "RightUpRight")
        {
            DisplayMessage("Okay, then what?", 50);
            Console.Clear();
            ShowPosition2();
            while(!isCorrect)
            {
            Console.Clear();
            _path = "";
            input = "";
            ShowPosition2();
            Console.WriteLine("Type 'Done' when you are finished.");
            while (input != "Done")
            {
            _path += input;
            input = Console.ReadLine();
            }

            if (_path == "LeftDownLeftLeftUpLeftUpLeft")
            {
                Console.Clear();
                DisplayMessage("I made it!", 50);
                DisplayMessage("I got you access to your ships's units. You can now control which is sent out.\nDon't ask how that works, whoever programmed this wanted to finish his project and go home. And his professor told him that he doesn't care about the UI. Get ready!", 50);
                isCorrect = true;
            }

            else
            {
                DisplayMessage("That's not right. Check again.", 50);
            }
            }
        }
        else
            {
                DisplayMessage("That's not right. Check again.", 50);
            }
        }
    }

    public override void GiveInstructions()
    {
        Console.WriteLine("In this activity, you need to keep track of where the 'X' goes and which exit he takes.");
        Console.WriteLine("You then need to direct the 'O' to where the room the 'X' was originally in, then to the exit it took.");
        Console.WriteLine("Put each direction in one at a time. Example: 'Up' *Enter* 'Down' *Enter*");
        Console.WriteLine("Then type 'Done' when you're finished.\n");
        Console.WriteLine("Press enter to continue.");
        Console.ReadLine();
        Console.Clear();
    }

